import numpy as np


class Solution:
    found = False
    def exist(self, board: [[str]], word: str) -> bool:

        # 1
        row_num = len(board)
        column_num = len(board[0])


        def search(x, y, visited, word):
            if self.found:
                return 1
            if word == "":
                #print(visited)
                self.found = True
                return 1
            candidates = [(x - 1, y), (x + 1, y), (x, y - 1), (x, y + 1)]
            for can in candidates:
                can_x = can[0]
                can_y = can[1]
                if can_x < 0 or can_x >= row_num or can_y < 0 or can_y >= column_num or visited[can_x][can_y] == 1:
                    # 越界了或被访问过
                    continue
                elif board[can_x][can_y] == word[0]:
                    # 匹配了当前首字符，继续遍历
                    # print("!!!")
                    # print(word[0])
                    visited[can_x][can_y] = 1
                    search(can_x, can_y, visited, word[1:])

            # 如果没匹配下一个字符，那么当前字符的visited 应改回去
            visited[x][y] = 0

        for i in range(row_num):
            for j in range(column_num):
                visited = np.zeros((row_num, column_num))

                # (i, j)搜索起始点
                if board[i][j] == word[0]:
                    # 匹配第一个字母，成功匹配的开始

                    print("!!")
                    visited[i][j] = 1
                    # 搜索这个元素的上下左右元素，看是否匹配第二个字符
                    search(i, j, visited, word[1:])
                    if self.found == True:
                        return True

        return False



